import {React,Fragment,useState} from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { REMOVE_FROM_CART } from '../../redux/actions/actions';

export default function ProductItem(props) {
    const product = props.product;
    const CartItems = useSelector(state => state.CartItemsReducer.CartItems);
    const dispatch = useDispatch();
    const [count, setCount] = useState(1);
    const Check = () => {
      if(count<=1) return;
      else setCount(count-1)
    };
    const RemoveItem = () =>{
      dispatch(REMOVE_FROM_CART(parseInt(product.product.id)))
    }
  return (
    <Fragment>
                    <li key={product.product.id} className="flex py-6 flex-col">
                                <div>
                                  
                                </div>
                                <span className='flex flex-row justify-end mb-2'>
                                        <div className="flex flex-row w-20  justify-evenly rounded-lg bg-transparent">
                                            <button data-action="decrement" className="bg-gray-300 text-gray-600 flex items-center justify-center hover:text-gray-700 hover:bg-gray-400 w-10 rounded-l cursor-pointer outline-none" onClick={()=> Check()}>
                                                <span className="m-auto text-2xl font-thin text-center">âˆ’</span>
                                            </button>
                                            <input type="number" className="focus:outline-none text-center w-full bg-gray-300 font-semibold text-md hover:text-black focus:text-black  md:text-basecursor-default flex items-center text-gray-700  outline-none" name="custom-input-number" value={`${count}`}></input>
                                            <button data-action="increment" className="bg-gray-300 text-center flex justify-center items-center  text-gray-600 hover:text-gray-700 hover:bg-gray-400 w-10 rounded-r cursor-pointer" onClick={()=> setCount(count+1)}>
                                                <span className="m-auto text-2xl font-thin">+</span>
                                            </button>
                                        </div>
                                    </span>
                              </li>
    </Fragment>
  )
}
