import { Types } from "../actions/types"
window.localStorage.setItem("Cart-items",JSON.stringify(state.CartItems))
const local = JSON.parse(window.localStorage.getItem("Cart-items"));
const foundData = (local.length==0)?[]:local;

const initialState = {
    CartItems : foundData
}
const CartItemsReducer = (state = initialState,action) =>
{
    switch(action.type)
    {
        case Types.ADD_TO_CART:
            /*
            const exist = state.CartItems.find((x)=> x.product.id === action.payload.product.id);
            if(exist){
                return state.CartItems.map((item)=> item.product.id === action.payload.product.id ? {...item,qty : item.qty + action.payload.qty}: item);
                //state.CartItems.map((item)=> item.product.id === action.payload.product.id ? {...item,qty : item.qty + action.payload.qty} : item);
            }
            else{
                state.CartItems.push(action.payload);
                return state;
            }*/
            /*state.CartItems.push(action.payload);
            return{
                ...state
            }*/
            state.CartItems.push(action.payload);
            window.localStorage.setItem("Cart-items",JSON.stringify(state.CartItems))
            return {
                ...state
            }
        case Types.REMOVE_FROM_CART:
            const NewCart = state.CartItems.filter((item) => item.id !== action.payload.id)
            state.CartItems = NewCart;
            return {
                ...state
            }
        default:
            return state;
    }
}

export default CartItemsReducer;
