import { Types } from "../actions/types"

const initialState = {
    CartItems : (localStorage.getItem('CartI_Items')!=null)?localStorage.getItem('CartI_Items'):[]
}
const CartItemsReducer = (state = initialState,action) =>
{
    switch(action.type)
    {
        case Types.ADD_TO_CART:
            state.CartItems.push(action.payload)
            localStorage.setItem('CartI_Items', JSON.stringify(state.CartItems));
        default:
            return state;
    }
}

export default CartItemsReducer;
