import { Types } from "../actions/types";

const initialState = {
    isLogin : false,
    UserToken : null,
} 

const UserReducer = (state = initialState,action) =>
{
    switch(action.type)
    {
        case Types.USER_LOGIN:
            return {
                ...state,
                isLogin : action.payload
            }
        case Types.SET_TOKEN :
            return {
                ...state,
                us
            }
        default : 
            return state;
    }
}

export default UserReducer;
