import { Types } from "../actions/types"

const initialState = {
    product : {},
    qty : 0
} 

const SelectedProduct = (state = initialState,action) =>
{
    switch(action.type)
    {
        case Types.SELETED_PRODUCT :
            return {
                ...state,
                product : action.payload
            };
        case Types.RMV_SELETED_PRODUCT:
            return {}
        default : 
            return state;
    }
}


export default SelectedProduct;
