import { CLOSE_CART, OPEN_CART } from "../actions/actions"

const initialState = {
    CartIsOpen : false
}

const CartReducer = (state = initialState , action) =>
{
    switch(action.type)
    {
        case OPEN_CART:
            return {
                CartIsOpen : true
            };
        case CLOSE_CART:
            return {
                CartIsOpen : false
            };
        default:
            return state;
    }
}

export default CartReducer;
