import { CLOSE_CART, OPEN_CART } from "../actions/actions"
import { Types } from "../actions/types";

const initialState = {
    CartIsOpen : false,
    CountItems : 0
}

const CartReducer = (state = initialState , action) =>
{
    switch(action.type)
    {
        case Types.OPEN_CART:
            return {
                CartIsOpen : true
            };
        case Types.CLOSE_CART:
            return {
                CartIsOpen : false
            };
        case Types.CHANGE_COUNT_CART :
            return {
                ...state,
                CountItems : state.CountItems + action.p
            }
        default:
            return state;
    }
}

export default CartReducer;
