import React, { useMemo } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { CHANGE_COUNT_CART } from '../../redux/actions/actions';

export default function CartCount() {
    const CartItems = useSelector(state => state.CartItemsReducer.CartItems);
    const CountCart = useSelector(state => state.CartReducer.CountItems);
    const dispatch = useDispatch();
    /*const Count = () =>
    {
        if(CartItems.length>=10) return "+10"
        else return CartItems.length
    }*/
    window.addEventListener('change',(e) =>
    {
        e.preventDefault();
        var count = 0;
        CartItems.map((item) => count += item.qty)
        dispatch(CHANGE_COUNT_CART(count))
    })
  return (
    <>
        <span className='flex justify-center items-center absolute top-0 right-0'>
                <span className='w-6 h-6 bg-indigo-400 rounded-full absolute text-xs flex justify-center items-center font-bold'>
                    {0 && CountCart}
                </span>
        </span>
    </>
  )
}
