export default function cartReduer(state, action)
{
    switch(action.type)
    {
        case ADD_TO_CART:{
            return{
                cart : [
                    ...state.cart,
                    {
                        product: action.prodactInfo,
                        quantity: action.quantity
                    }
                ]
            }
        }
        default:
            return state;
    }
}
