import {React,Fragment,useState} from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { REMOVE_FROM_CART } from '../../redux/actions/actions';

export default function ProductItem(props) {
    const product = props.product;
    const CartItems = useSelector(state => state.CartItemsReducer.CartItems);
    const dispatch = useDispatch();
    const [count, setCount] = useState(1);
    const Check = () => {
      if(count<=1) return;
      else setCount(count-1)
    };
    const RemoveItem = () =>{
      dispatch(REMOVE_FROM_CART(parseInt(product.product.id)))
    }
  return (
    <Fragment>
                    <li key={product.product.id} className="flex py-6">
                                <div className="h-24 w-24 flex-shrink-0 overflow-hidden rounded-md border border-gray-200">
                                  <img
                                    src={product.product.thumbnail}
                                    alt={product.product.title}
                                    className="h-full w-full object-cover object-center"
                                  />
                                </div>

                                <div className="ml-4 flex flex-1 flex-col">
                                  <div>
                                    <div className="flex justify-between text-base font-medium text-gray-900">
                                      <h3>
                                        <a href={product.product.href}> {product.product.title} </a>
                                      </h3>
                                      <p className="ml-4">${product.product.price}</p>
                                    </div>
                                    <p className="mt-1 text-sm text-gray-500">{product.product.category}</p>
                                    <span className='flex flex-col gap-5 justify-center items-center'>
                                        <div className="flex flex-col h-5 w-20 rounded-lg relative bg-transparent mt-1">
                                            <button data-action="decrement" className="bg-gray-300 text-gray-600 hover:text-gray-700 hover:bg-gray-400 h-full w-20 rounded-l cursor-pointer outline-none" onClick={()=> Check()}>
                                                <span className="m-auto text-2xl font-thin">−</span>
                                            </button>
                                            <input type="number" className="focus:outline-none text-center w-full bg-gray-300 font-semibold text-md hover:text-black focus:text-black  md:text-basecursor-default flex items-center text-gray-700  outline-none" name="custom-input-number" value={`${count}`}></input>
                                            <button data-action="increment" className="bg-gray-300 text-gray-600 hover:text-gray-700 hover:bg-gray-400 h-full w-20 rounded-r cursor-pointer" onClick={()=> setCount(count+1)}>
                                                <span className="m-auto text-2xl font-thin">+</span>
                                            </button>
                                        </div>
                                    </span>
                                  </div>
                                  <div className="flex flex-1 items-end justify-between text-sm">
                                    <p className="text-gray-500">Qty {product.qty}</p>
                                    <div className="flex">
                                      <button
                                        type="button"
                                        className="font-medium text-indigo-600 hover:text-indigo-500"
                                        onClick={() => RemoveItem()}
                                      >
                                        Remove
                                      </button>
                                    </div>
                                  </div>
                                </div>
                              </li>
    </Fragment>
  )
}
